<script lang="ts">
    import { invoke } from "@tauri-apps/api/core";
    import type { User, ScreenSize, Server, ServerInfo, ConnectRDPParams } from '../types';
    import { open } from '@tauri-apps/plugin-dialog';
    //import { open } from '@tauri-apps/api/dialog';
    import { user, serverdata, activeTab } from '../store';
	import { onMount } from 'svelte';
  
    
    let username: string = "";
    let password: string = "";
    let additional_data: string = "";
    let remember = false;
    let useSSHKey = false;
    

    async function get_cred() {
        let tunnelid = $serverdata.service.id;
        let companyid = $serverdata.companyid;
        try {
            // Указываем тип данных, которые ожидаем получить: либо кортеж из двух строк, либо null
            let logindata: [string, string, string] | null = await invoke('get_login_data', { tunnelid,});

            if (logindata != null) {
                // Присваиваем значения из кортежа
                username = logindata[0];
                password = logindata[1];
                additional_data = logindata[2];
                if (additional_data != "") {
                  useSSHKey=true
                }
                remember = true;
            } else {
                console.log("Login data not found");
            }
        } catch (error) {
            console.error("Error fetching login data:", error);
        }
    }

    function handleServerLogin() {
        
        activeTab.set('server'); // Переключаем вкладку на loginserver
    }

    async function singInRdp() {
        let domain = additional_data;
        let tunnelid = $serverdata.service.id;
        let tunnelnamme = $serverdata.service.protocol;
        await invoke('start_rdp', { tunnelid, username, password, domain, remember});
        handleServerLogin()
    }

    async function singInSsh() {
        let filekey = "";
        if (useSSHKey && additional_data != "") {
          filekey = additional_data;
        };
        let tunnelid = $serverdata.service.id;
        let tunnelnamme = $serverdata.service.protocol;
        await invoke('start_ssh', { tunnelid, username, password, filekey, remember});
        handleServerLogin()
    }


    function handleConfig() {
        
        activeTab.set('configserver'); // Переключаем вкладку на loginserver
    }

    async function selectFile() {
      try {
          const file = await open({
          multiple: false,
          filters: [{ name: "ключ", extensions: ['pem', 'pub'] }]
          });
          

          if (file) {
            additional_data = String(file);
          }
      } catch (error) {
          console.error('Error selecting file:', error);
      }
    }
    function handleSvgClick(localport: number) {
      const textToCopy = `localhost:${localport}`;
      navigator.clipboard.writeText(textToCopy).then(() => {
          showNotification('Скопировано!');
      }).catch(err => {
          console.error('Failed to copy text: ', err);
      });
    }
    function showNotification(message) {
        const notification = document.getElementById('notification');
        notification.textContent = message;
        notification.style.display = 'block';

        // Скрыть уведомление через 3 секунды
        setTimeout(() => {
            notification.style.display = 'none';
            activeTab.set('server');
        }, 3000);
        
         
    }
    onMount(get_cred)

</script>



