                <image id="image0_671_530" width="800" height="800" xlink:href="data:image/png;base64,"/>
                </defs>
                </svg>
                
                
            </div>
  
            
  
            
        </div>
        <div >
            <button type="submit" class="custom-button" on:click="{singInRdp}">Войти</button>
          </div>

          
      </form>
      <br>
      <div class="copy-info">
        localhost:{$serverdata.status.rdp_param.localport}
