            {/if}
          
          
  
          
          
        </div>
        <div class="custom-data-2">
            <input type="checkbox" class="custom-checkbox" bind:checked={remember}>
            Сохранить
            
        </div>
        <div >
            <button type="submit" class="custom-button" on:click="{singInSsh}">Войти</button>
        </div>
        
      </form>
      <br>


      {#if $serverdata.service.protocol == 'rdp'}
      <div class="copy-info">
        localhost:{$serverdata.status.rdp_param.localport}
