          <path d="M17.4166 10H18.3333C18.8195 10 19.2858 9.85952 19.6297 9.60947C19.9735 9.35942 20.1666 9.02028 20.1666 8.66666V2.66666C20.1666 2.31304 19.9735 1.9739 19.6297 1.72385C19.2858 1.4738 18.8195 1.33333 18.3333 1.33333H10.0833C9.59706 1.33333 9.13075 1.4738 8.78693 1.72385C8.44311 1.9739 8.24996 2.31304 8.24996 2.66666V3.33333M11.9166 6H3.66663C2.6541 6 1.83329 6.59695 1.83329 7.33333V13.3333C1.83329 14.0697 2.6541 14.6667 3.66663 14.6667H11.9166C12.9291 14.6667 13.75 14.0697 13.75 13.3333V7.33333C13.75 6.59695 12.9291 6 11.9166 6Z" stroke="#F3F3F3" stroke-width="1" stroke-linecap="round" stroke-linejoin="round"/>
